import React, { useState } from 'react';
import "./style.css";
import Status from "./Status";
import Lists from "./Lists";
import AddTask from "./AddTask";

const initialList = [
  {
    title: 'react module',
    status: 'complete',
  },
  {
    title: 'components in react',
    status: 'incomplete',
  }
]

const App = () => {
  const [listitems, setListitems] = useState(initialList);
  const [filter, setFilter] = useState(null);
  const [isChecked,setIsChecked] = useState(false);

  const deleteTask = (task) => {
    const title = task.title
    const deletedTask = listitems.filter((item) => item.title !== title);
    setListitems(deletedTask);
  }

  const handleFilter = (event) => {
    const selectedFilter = event.target.value;
    setFilter(selectedFilter);
  }

  const handleAddTask = (text) => {
    setListitems([
      ...listitems,
      {
        title: text,
        status: 'incomplete',
      }
    ])
  }

const handleOnChange = (item) => {
  if (item.target.Checked === true) {
       setIsChecked(true);
    // const checkedItem = (item.status === 'complete')
    // setFilter(checkedItem);
  } else {
      // const unCheckedItem = (item.status === 'incomplete')
      setIsChecked(false);
      // setFilter(unCheckedItem);
  }
}

 let filteredlist = [...listitems]
  if (filter && filter !== "All") {
    filteredlist = listitems.filter((item) => {
      return item.status === filter;
    })
  }


  return (
    <div>
      <h1>ToDOLIST</h1>
      <AddTask onAddTask={handleAddTask} />
      <Lists listitems={filteredlist}
        deleteTask={deleteTask} />
        {/* // handleOnChange = {handleOnChange}  */}
      <Status handleFilter={handleFilter} />
    </div>
  );
}

export default App;
