import React, { useState } from 'react';
 import "./style.css";
import Status from "./Status";
import Lists from "./Lists";
import AddTask from "./AddTask";


let nextId = 1;

const initialList = [
  {
    title: 'react module',
    status: 'complete',
  },
  {
    title: 'components in react',
    status: 'incomplete',
  }
]

export default function App() {
  const [listitems, setListitems] = useState(initialList);
  const [filteredListItems, setFilteredListItems] = useState([]);

  const deleteTask = (task) => {
    const title = task.title
    const deletedTask = listitems.filter((item) => item.title !== title);
    setListitems(deletedTask);
  }

  // let tempList = [];

  // const handleOnStatusChange = () => {
  //   const selectedItem = ev
  // }

  const handleFilter = (event) => {
    const selectedFilter = event.target.value;
    console.log(selectedFilter);
    if (selectedFilter === 'All') {
      setFilteredListItems(listitems);

    } else {
      console.log(listitems);
      const filteredlist = listitems.filter((item) => {
        return item.status === selectedFilter;
      })
      setFilteredListItems(filteredlist)
    }
  }

  const handleAddTask = (text) => {
    setListitems([
      ...listitems,
      {
        title: text,
        status: 'incomplete',
      }
    ])
    if (filteredListItems.length > 0 ) {
      setFilteredListItems([
        ...filteredListItems,
        {
          title: text,
        status: 'incomplete',
        }
      ])

    }
    // console.log("current list "+listitems);
  }



  return (
    <div>
      <h1>ToDOLIST</h1>
      <AddTask onAddTask={handleAddTask} />
      <Lists listitems={filteredListItems.length > 0 ? filteredListItems : listitems}
       deleteTask = {deleteTask} />
      <Status handleFilter={handleFilter} />

    </div>
  );
}
